mapper.select:
select 
from message where id in (
SELECT MAX(id) FROM message
WHERE STATUS != 2
AND from_id != 1
AND (from_id = #{userId} or to_id = #{userId})
GROUP BY conversation_id
)
order by id desc
limit #{offset},#{limit}

select count(id)
from message
where status = 0
and from_id != 1
and to_id = #{userId}

    and conversation_id = #{conversationId}
